package com.validation.sandbox.api.service.impl;

import static org.junit.Assert.assertThrows;
import static org.mockito.Mockito.mock;

import java.io.IOException;
import java.security.GeneralSecurityException;
import java.security.cert.CertificateException;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.Mockito;
import org.mockito.junit.jupiter.MockitoExtension;

import com.validation.sandbox.api.exception.InvalidCertificateException;

@ExtendWith(MockitoExtension.class)
public class CertificateNameValidatorTest {

	@InjectMocks
	private CertificateNameValidator certificateNameValidator;
	
	private String signature = null;

	private String xRequestId = null;
	
	private String signatureCertificate =null;
	
	private String unknownCertificate =null;

	@BeforeEach
	public void setUp() {
		signature = "";

	    xRequestId = "29318e25-cebd-498c-888a-f77672f66449";
	    
	    signatureCertificate ="-----BEGIN CERTIFICATE----------END CERTIFICATE-----";
	    
	    unknownCertificate ="-----BEGIN CERTIFICATE----------END CERTIFICATE-----";
	}

	@Test
	public void validCertificateNameTest() throws IOException, GeneralSecurityException {

		CertificateNameValidator certificateNameValidator = mock(CertificateNameValidator.class);

		Mockito.lenient().doNothing().when(certificateNameValidator).validateCertificateName(signature, signatureCertificate,
				xRequestId);
	}

	@Test
	public void invalidCertificateNameTest() throws CertificateException, IOException {

		assertThrows(InvalidCertificateException.class,
				() -> certificateNameValidator.validateCertificateName(signature, unknownCertificate,
						xRequestId));

	}

}
